    private void btnSeleccionArchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSeleccionArchActionPerformed
        int valorOpcion = this.jfcSeleccionadorArch.showOpenDialog(this);
        if (valorOpcion == 0) {
            txtArchivoConfig.setText(this.jfcSeleccionadorArch.getSelectedFile().getAbsolutePath());
        }
    }//GEN-LAST:event_btnSeleccionArchActionPerformed

    private void btnValidarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnValidarActionPerformed
        try {
            if (automata != null) {
                Resultado resultado = automata.ejecutar(this.txtInput.getText());
                if (resultado.isAceptada()) {
                    lblResultado.setForeground(new Color(0, 160, 0));
                    lblResultado.setText("Cadena aceptada!!");
                } else {
                    lblResultado.setForeground(Color.red);
                    lblResultado.setText("Cadena rechazada!!");
                }
                txtLog.setForeground(Color.black);
                txtLog.setText(new String(resultado.getLog()));
            } else {
                lblResultado.setForeground(Color.red);
                lblResultado.setText("Aut\u00f3mata sin generar.");
                txtLog.setForeground(Color.red);
                txtLog.setText("Debe generar un aut\u00f3mata antes de validar la entrada!!");
            }
        }
        catch (Exception exc) {
            lblResultado.setForeground(Color.red);
            lblResultado.setText("Error al validar la cadena de entrada!!");
            txtLog.setForeground(Color.red);
            txtLog.setText((exc.getMessage()==null)?exc.getCause().getMessage():exc.getMessage());
            exc.printStackTrace();
        }
    }//GEN-LAST:event_btnValidarActionPerformed

    private void btnGenerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGenerarActionPerformed
        try {
            String mensaje = null;
            lblResultado.setForeground(new Color(0, 160, 0));
            mensaje = (automata == null)?"Aut\u00f3mata generado.":"Se ha generado un nuevo Aut\u00f3mata.";
            automata = new Automata(this.txtArchivoConfig.getText());
            lblResultado.setText(mensaje);
        }
        catch (Exception exc) {
            lblResultado.setForeground(Color.red);
            lblResultado.setText("Fall\u00f3 la generaci\u00f3n del aut\u00f3mata!!");
            txtLog.setForeground(Color.red);
            txtLog.setText((exc.getMessage()==null)?exc.getCause().getMessage():exc.getMessage());
            exc.printStackTrace();
        }
    }//GEN-LAST:event_btnGenerarActionPerformed

